const homeNav = document.getElementById("homeNav");
const aboutNav = document.getElementById("aboutNav");
const projectsNav = document.getElementById("projectsNav");
const contactNav = document.getElementById("contactNav");

const homeNavMobile = document.getElementById("homeNavMobile");
const aboutNavMobile = document.getElementById("aboutNavMobile");
const projectsNavMobile = document.getElementById("projectsNavMobile");
const contactNavMobile = document.getElementById("contactNavMobile");

const home = document.getElementById("home");
const about = document.getElementById("about");
const projects = document.getElementById("projects");
const contact = document.getElementById("contact");
const footer = document.getElementById("footer");

function Home() {
  home.scrollIntoView({ behavior: 'smooth' }); BodyScrollOn();
}
function About() {
  about.scrollIntoView({ behavior: 'smooth' }); BodyScrollOn();
}
function Projects() {
  projects.scrollIntoView({ behavior: 'smooth' }); BodyScrollOn();
}
function Contact() {
  contact.scrollIntoView({ behavior: 'smooth' }); BodyScrollOn();
}

function ScrollNavStyle() {
  let scrollOffset = scrollY + 101;
  //Home Section Scroll
  if (home.offsetHeight > scrollOffset) {
    homeNav.classList.add("nav-item-active");
    homeNavMobile.classList.add("nav-item-active");
  }
  else {
    homeNav.classList.remove("nav-item-active");
    homeNavMobile.classList.remove("nav-item-active");
  }
  //About Section Scroll
  if (home.offsetHeight + about.offsetHeight > scrollOffset && home.offsetHeight < scrollOffset) {
    aboutNav.classList.add("nav-item-active");
    aboutNavMobile.classList.add("nav-item-active");
  }
  else {
    aboutNav.classList.remove("nav-item-active");
    aboutNavMobile.classList.remove("nav-item-active");
  }
  //Projects Section Scroll
  if (home.offsetHeight + about.offsetHeight + projects.offsetHeight > scrollOffset && home.offsetHeight + about.offsetHeight < scrollOffset) {
    projectsNav.classList.add("nav-item-active");
    projectsNavMobile.classList.add("nav-item-active");
  }
  else {
    projectsNav.classList.remove("nav-item-active");
    projectsNavMobile.classList.remove("nav-item-active");
  }
  //Contact Section Scroll
  if (home.offsetHeight + about.offsetHeight + projects.offsetHeight + contact.offsetHeight + footer.offsetHeight > scrollOffset && home.offsetHeight + about.offsetHeight + projects.offsetHeight < scrollOffset) {
    contactNav.classList.add("nav-item-active");
    contactNavMobile.classList.add("nav-item-active");
  }
  else {
    contactNav.classList.remove("nav-item-active");
    contactNavMobile.classList.remove("nav-item-active");
  }
}

const mobileNavContent = document.getElementById("mobileNavContent");

function BodyScrollOff() {
  document.body.style.overflow = "hidden";
  mobileNavContent.style.pointerEvents = "auto";
}
function BodyScrollOn() {
  mobileNavContent.style.pointerEvents = "none";
  setTimeout(function () { document.body.style.overflow = "auto"; }, 400);
}

window.onload = function () { ScrollNavStyle(); };

window.onscroll = function () { ScrollNavStyle(); };


