function getCertificates() {
    fetch('https://jeffersondc.netlify.app/api/certificate')
        .then(function (res) {
            return res.json();
        }).then(function (data) {
            let certificateInfos = '';

            data.forEach((certificate, index) => {
                certificateInfos += `
                    <div id="certificate" class="d-flex flex-column flex-wrap align-items-center">
                        <div class="px-0" data-aos="zoom-in" data-aos-delay="400">
                            <img class="w-100" src="${certificate.image}" href="${certificate.image}" data-featherlight="iframe" />
                        </div>
                </div>`;
            });


            document.getElementById('certificatesSlider').innerHTML = certificateInfos;

            /* SLIDER OWL CAROUSEL */
            $('.certificates-carousel').owlCarousel({
                loop: true,
                margin: 50,
                autoplay: false,
                autoHeight: false,
                touchDrag: true,
                mouseDrag: false,
                dots: false,
                nav: true,
                navText: ['<span class="lnr lnr-chevron-left"></span> </div>', '<span class="lnr lnr-chevron-right"></span>'],
                responsiveClass: true,
                responsive: {
                    0: {
                        items: 1,
                        slideBy: 1
                    },
                    768: {
                        items: 2,
                        slideBy: 2
                    },
                    1200: {
                        items: 3,
                        slideBy: 3
                    }
                }
            });

            AOS.init({ duration: "1000", once: true });
        })
        .catch(function (err) {
            console.log(err);
        });
}

getCertificates();