function getProjects() {
    fetch('https://jeffersondc.netlify.app/api/project')
        .then(function (res) {
            return res.json();
        }).then(function (data) {
            let output = '';
            data.forEach(function (project) {
                output += `
                    <div id="project" class="d-flex flex-row flex-wrap align-items-center">
                        <div class="col-lg d-flex align-items-center justify-content-center px-0">
                            <img class="w-100" src="${project.image}" />
                            <div id="projectLinkBackdrop">
                                <a href="${project.link}" target="_blank" id="projectLink"> See Project </a>
                            </div>
                        </div>
                        <div class="col-lg px-0">
                            <h2 class="text-left"> ${project.title} </h2>
                            <p class="text-left mt-2"> ${project.description} </p>
                            <div class="col-lg d-flex flex-row flex-wrap justify-content-start align-items-start mt-3 p-0"
                                style="gap: 0.75em;">
                                <div class="skill d-flex align-items-center"> {} </div>
                            </div>
                        </div>
                    </div>
                `;
            });
            document.getElementById('projectsSlider').innerHTML = output;
            if (output != '') {
                AOS.init({ duration: "1000", once: true });
            }
                
        })
        .catch(function (err) {
            console.log(err);
        });
}

getProjects();