function getProjects() {
    fetch('https://jeffersondc.netlify.app/api/project')
        .then(function (res) {
            return res.json();
        }).then(function (data) {
            let projectInfos = '';
            data.forEach(function (project) {
                projectInfos += `
                    <div id="project" class="d-flex flex-row flex-wrap align-items-center">
                        <div class="col-lg d-flex align-items-center justify-content-center px-0">
                            <img class="w-100" src="${project.image}" />
                            <div id="projectLinkBackdrop">
                                <a href="${project.link}" target="_blank" id="projectLink"> See Project </a>
                            </div>
                        </div>
                        <div class="col-lg px-0">
                            <h2 class="text-left"> ${project.title} </h2>
                            <p class="text-left mt-2"> ${project.description} </p>
                            <div class="col-lg d-flex flex-row flex-wrap justify-content-start align-items-start mt-3 p-0" style="gap: 0.75em;">`
                            + 
                                data.forEach(function (project) {
                                    `<div class="skill d-flex align-items-center"> ${project.skills[1]} </div>`
                                }) 
                            +
                            `</div>
                        </div>
                    </div>`;
            });

            
            document.getElementById('projectsSlider').innerHTML = projectInfos;
            
            /* SLIDER OWL CAROUSEL */
            $('.projects-carousels').owlCarousel({
                items: 1,
                loop: true,
                margin: 10,
                singleItem: true,
                autoplay: true,
                autoHeight:true,
                autoplayTimeout: 5000,
                autoplayHoverPause: true,
                touchDrag  : false,
                mouseDrag  : false,
                nav: true,
                navText: ['<span class="lnr lnr-chevron-left"></span> </div>', '<span class="lnr lnr-chevron-right"></span>']
            })  
        })
        .catch(function (err) {
            console.log(err);
        });
}

getProjects();