function getProjects() {
    fetch('https://jeffersondc.netlify.app/api/project')
        .then(function (res) {
            return res.json();
        }).then(function (data) {
            let projectDiv = '';
            let projectInfos = '';
            let projectSkills = '';
            let projectEnd = '';

            data.forEach((project, index) => {
                projectInfos = `
                    <div id="project" class="d-flex flex-column flex-wrap align-items-center">
                        <div class="px-0" data-aos="zoom-in" data-aos-delay="400">
                            <img class="w-100" src="${project.image}" />
                            <div id="projectLinkBackdrop">
                                <a href="${project.link}" target="_blank" id="projectLink"> See Project </a>
                            </div>
                        </div>
                        <div class="px-0" data-aos="fade-in" data-aos-delay="600">
                            <h2 class="text-left"> ${project.title} </h2>
                            <p class="text-left mt-2"> ${project.description} </p>
                            <div class="col-lg d-flex flex-row flex-wrap justify-content-start align-items-start mt-3 p-0" style="gap: 0.75em;">`;

                project.skills.forEach(skills => {
                    projectSkills += `<div class="skill d-flex align-items-center"> ${skills} </div>`;
                });

                projectEnd = `</div>
                    </div>
                </div>`;

                projectDiv += projectInfos + projectSkills + projectEnd;
                projectSkills = '';
            });


            document.getElementById('projectsSlider').innerHTML = projectDiv;

            /* SLIDER OWL CAROUSEL */
            $('.projects-carousel').owlCarousel({
                items: 3,
                loop: true,
                margin: 50,
                singleItem: true,
                autoplay: true,
                autoHeight: false,
                autoplayTimeout: 5000,
                autoplayHoverPause: true,
                touchDrag: true,
                mouseDrag: false,
                dots:true,
                responsiveClass: true,
                responsive: {
                    0: {
                        items: 1
                    },
                    768: {
                        items: 2
                    }
                }
            });

            AOS.init({ duration: "1000", once: true });
        })
        .catch(function (err) {
            console.log(err);
        });
}

getProjects();